mapper.select:
SELECT power_unit, sum( Gg_value ) AS Gg, sum( MRTBF_value ) AS MRTBF, sum( complex_value ) AS complex FROM jb_power_unit_data where 1=1
and station_id=#{stationId}
and DATE_FORMAT( `data_time`, "%Y-%m" )= #{yearMonth}
GROUP BY power_unit

SELECT power_unit yData1, ghi_value xData, avg( power_value ) yData FROM jb_power_unit_kw where 1=1
and station_id=#{stationId}
and DATE_FORMAT( `data_time`, "%Y-%m" )= #{yearMonth}
and power_unit in
#{item}
GROUP BY power_unit, ghi_value

SELECT inverter,warning_time,warning_type,warning_desc FROM jb_power_inverter_warning GROUP BY inverter,warning_time,warning_type,warning_desc
    WHERE 1=1

    station_id=#{stationId}

SELECT  *  FROM jb_power_inverter_warning
WHERE 1=1

 and   inverter=#{inverter}


    and   warning_time=#{warningTime}


    and   warning_type=#{warningType}


    and   warning_desc=#{warningDesc}
