mapper.select:
select inter.id as id, inter.org_id as code, inter.inter_type as interType, inter.inter_name as interName, inter.inter_desc as interDesc, inter.inter_url as interUrl from jb_model_interface_rel rel inner join jb_report_interface inter on inter.id=rel.inter_id
and rel.model_id=#{modelId}
inner join jb_report_model model on model.id=rel.model_id where 1=1
and inter.inter_type=#{interType}
and model.model_name like concat('%', #{modelName}, '%')

select count(*) as cnt from jb_model_interface_rel rel inner join jb_report_interface inter on inter.id=rel.inter_id
and rel.model_id=#{modelId}
inner join jb_report_model model on model.id=rel.model_id where 1=1
and inter.inter_type=#{interType}
and model.model_name like concat('%', #{modelName}, '%')
