mapper.select:
SELECT station.station_id AS stationId, station.station_type AS stationType, model.id AS modelId, model.report_name AS reportName, model.model_file_url AS modelFileUrl, model.model_version AS modelVersion FROM jb_station_model_rel rel INNER JOIN jb_report_model model ON rel.model_id = model.id and model.status=1 INNER JOIN jb_station station ON rel.station_id = station.station_id AND station.is_show = 1 where not EXISTS (select 1 from jb_report_mng mng where mng.station_id=rel.station_id
and mng.report_year =#{year}
and mng.report_quarter =#{quarter}
)

SELECT mng.id, mng.report_year as reportYear, mng.report_quarter as reportQuarter, mng.station_id AS stationId, mng.station_type AS stationType FROM jb_station_model_rel rel INNER JOIN jb_report_model model ON rel.model_id = model.id and model.status=1 INNER JOIN jb_station station ON rel.station_id = station.station_id AND station.is_show = 1 INNER JOIN jb_report_mng mng ON mng.station_id = rel.station_id
AND mng.report_status = #{reportStatus}
order by status_time asc

SELECT inter.inter_url AS interUrl, inter.place_tag AS placeTag, inter.pid FROM jb_report_interface inter INNER JOIN jb_model_interface_rel inter_rel ON inter.id = inter_rel.inter_id where 1=1
AND inter.inter_type = #{modelType}
AND inter_rel.model_id = #{modelId}

SELECT inter.inter_url AS interUrl, inter.place_tag AS placeTag, inter.pid FROM jb_report_interface inter INNER JOIN jb_model_interface_rel inter_rel ON inter.id = inter_rel.inter_id where 1=1
AND inter.inter_type = #{modelType}
AND inter_rel.model_id = #{modelId}
